def find_country(text):


    words = re.findall(r'\b\w+\b', text)

    for country, aliases in countries.items():
        for alias in aliases:
            for word in words:
                if alias==word.lower():
                    return country
    return None

def find_countries(text):
    cou=[]
    words = re.findall(r'\b\w+\b', text)

    for country, aliases in countries.items():
        for alias in aliases:
            for word in words:
                if alias==word.lower():
                
                    cou.append(country)
    return cou

